import React from 'react'
import wx from 'weixin-js-sdk'

class Bus extends React.Component {
  componentDidMount(){
    this.props.onRef(this)
    console.log(this) // ->将child传递给this.props.onRef()方法
  }
  navigateTo(url) {
    wx.miniProgram.navigateTo({
      url
    })
  }
  render() {
    return ''
  }
}

export default Bus
